from selenium import webdriver
from robot.libraries.BuiltIn import BuiltIn
from os import path


def set_capabilities(view_mode):
    country_code = BuiltIn().get_variable_value("${country_code}").upper()
    test_suite = BuiltIn().get_variable_value("${SUITE NAME}")
    path_to_profile = path.join(BuiltIn().get_variable_value('${EXEC_DIR}'), 'Misc', 'ff_profile', '1jns4mqv.har_log')
    run_mode = BuiltIn().get_variable_value("${RUN_MODE}")
    browser = BuiltIn().get_variable_value("${BROWSER}")
    timeouts = {
        "script": 10000,
        "pageLoad": 60000,
        "implicit": 3000
    }
    selenoid_opt = {
        "name": f"{test_suite} on {country_code}, {run_mode}",
        "enableVNC": True,
        "enableLog": False
    }
    # |==================== F I R E F O X : ========================|
    profile_obj = webdriver.FirefoxProfile(path_to_profile)
    firefox_options = webdriver.firefox.options.Options()
    firefox_options.set_preference("media.navigator.permission.disabled", True)
    firefox_options.set_preference("geo.enabled", False)
    firefox_options.set_preference("geo.provider.use_corelocation", False)
    firefox_options.set_preference("geo.prompt.testing", True)
    firefox_options.set_preference("geo.prompt.testing.allow", True)
    firefox_options.set_preference("media.navigator.enabled", True)
    firefox_options.set_preference("media.navigator.streams.fake", True)
    firefox_options.set_preference("browser.newtabpage.directory.source", "data:application\/json,{}")
    firefox_options.set_preference("browser.newtabpage.directory.ping", "")
    firefox_options.set_preference("browser.newtab.preload", False)
    firefox_options.set_preference("network.dns.disableIPv6", True)
    firefox_options.set_preference("app.update.auto", False)
    firefox_options.set_preference("app.update.enabled", False)
    firefox_options.set_preference("datareporting.healthreport.about.reportUrl",
                                   "http=>\/\/%(server)s\/dummy\/abouthealthreport\/")
    firefox_options.set_preference("datareporting.healthreport.documentServerURI",
                                   "http=>\/\/%(server)s\/dummy\/healthreport\/")
    firefox_options.set_preference("datareporting.healthreport.logging.consoleEnabled", False)
    firefox_options.set_preference("datareporting.healthreport.service.enabled", False)
    firefox_options.set_preference("datareporting.healthreport.service.firstRun", False)
    firefox_options.set_preference("datareporting.healthreport.uploadEnabled", False)
    firefox_options.set_preference("datareporting.policy.dataSubmissionEnabled", False)
    firefox_options.set_preference("datareporting.policy.dataSubmissionPolicyAccepted", False)
    firefox_options.set_preference("datareporting.policy.dataSubmissionPolicyBypassNotification", True)
    firefox_options.set_preference("extensions.installDistroAddons", False)
    firefox_options.set_preference("extensions.showMismatchUI", False)
    firefox_options.set_preference("extensions.update.enabled", False)
    firefox_options.set_preference("extensions.update.notifyUser", False)
    firefox_options.set_preference("devtools.netmonitor.persistlog", True)
    firefox_options.set_preference("devtools.toolbox.selectedTool", "webconsole")
    firefox_options.set_preference("devtools.toolbox.sidebar.width", 207)
    firefox_options.set_preference("devtools.toolbox.host", "right")
    # firefox_options.set_preference("devtools.toolbox.footer.height", 0)
    firefox_options.add_argument("-devtools")
    firefox_desired_caps = firefox_options.to_capabilities()
    firefox_desired_caps["browserName"] = "firefox"
    firefox_desired_caps["version"] = "99.0"
    firefox_desired_caps["platform"] = "LINUX"
    firefox_desired_caps["acceptInsecureCerts"] = True
    firefox_desired_caps["selenoid:options"] = selenoid_opt
    firefox_desired_caps["screenResolution"] = "1920x1080"
    # |====================== C H R O M E : ========================|
    chrome_options = webdriver.chrome.options.Options()
    chrome_options.add_encoded_extension(
        "")
    chrome_options.add_argument("auto-open-devtools-for-tabs")
    chrome_options.add_argument("disable-features=IsolateOrigins,site-per-process")
    chrome_options.add_argument("disable-geolocation")
    chrome_options.add_argument("disable-gpu")
    chrome_options.add_argument("no-pings")
    chrome_options.add_argument("no-sandbox")
    chrome_options.add_argument("no-experiments")
    chrome_options.add_argument("disable-translate")
    chrome_options.add_argument("disable-popup-blocking")
    chrome_options.add_argument("log-level=3")
    chrome_options.add_argument("silent")
    chrome_options.add_argument("devtools-replay=false")
    chrome_options.add_argument("disable-logging")
    chrome_options.add_argument("disable-features=IsolateOrigins,site-per-process")
    chrome_options.add_experimental_option("excludeSwitches",
                                           ["disable-component-update", "enable-logging", "enable-automation"])
    chrome_options.add_experimental_option("prefs", {
        "profile.default_content_setting_values.media_stream_camera": 1,
        "profile.default_content_setting_values.popups": 1,
        "profile.default_content_setting_values.geolocation": 2,
        "credentials_enable_service": False,
        "profile.password_manager_enabled": False,
        "devtools.preferences.currentDockState": "\"right\"",
        "devtools.preferences.panel-selectedTab": "\"console\"",
        "devtools.preferences": {"InspectorView.splitViewState": "{\"vertical\":{\"size\":0.1}}"}})

    if view_mode == "mobile_view":
        chrome_options.add_experimental_option("mobileEmulation", {"deviceName": "iPhone X"})

    chrome_desired_caps = chrome_options.to_capabilities()
    if browser == "OPERA":
        chrome_desired_caps["browserName"] = "opera"
        chrome_desired_caps["version"] = "85.0"
    else:
        chrome_desired_caps["browserName"] = "chrome"
        chrome_desired_caps["version"] = "106.0"
    chrome_desired_caps["platform"] = "ANY"
    chrome_desired_caps["screenResolution"] = "1920x1080"
    chrome_desired_caps["acceptInsecureCerts"] = True
    chrome_desired_caps["selenoid:options"] = selenoid_opt
    return chrome_desired_caps
